htmx.defineExtension('dialog-tag-confirm',{onEvent:function(a,b){if(a!=='htmx:confirm')return;if(!b.detail?.question)return;b.preventDefault();var c=document.querySelector('#dialog-tag-confirm'),d=document.importNode(c.content,!0);if(!c)throw Error('Template #dialog-tag-confirm not found!');let e=d.querySelector('[slot=question]');e.innerHTML=b.detail.question;let f=d.querySelector('dialog');document.body.appendChild(d);f.showModal();f.addEventListener('close',()=>{f.remove();if(f.returnValue!=='confirmed')return;b.detail.issueRequest(!0)})}});
