htmx.defineExtension('dialog-tag-confirm',{onEvent:function(a,b){if(a!=='htmx:confirm')return;if(!b.detail?.question)return;b.preventDefault();var c=b.detail.elt,d=c.dataset.confirmTemplate??'#dialog-tag-confirm',e=document.querySelector(d),f=document.importNode(e.content,!0);if(!e)throw `Template '${d}' not found!`;let g=f.querySelector('[slot=question]');g.innerHTML=b.detail.question;let h=f.querySelector('dialog');document.body.appendChild(f);h.showModal();h.addEventListener('close',()=>{h.remove();if(h.returnValue!=='confirmed')return;b.detail.issueRequest(!0)})}});
